import { React, Component } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { getUnits, addUser, getUsers } from "../../services/adminService";
import { getFacturesOfUser } from "../../services/elbahjaService";
import "bootstrap/dist/css/bootstrap.css";

class Electric extends Component {

    
  state = {
    id: this.props.match.params.id,
    users: [],
    factures:[],
    show:false
  };

  async getFactures(id){
    const { data: factures } = await getFacturesOfUser(id);
    this.setState({factures:factures})
    console.log(factures);
  }

  async getUsers() {
    const { data: users } = await getUsers();
    this.setState({ users: users });
  }

  async componentDidMount() {

    await this.getFactures(this.state.id);
    console.log(this.state.factures);
  }

  showModal(){
    var modall = document.getElementById("myModal");
    modall.modal('show');
  }

  show(){
    this.show = true;
  }

  handleShow(){
    this.show = false;
  }


  render() {
    return (
      <div>
        <button onClick={ () => this.showModal()}>Show</button>
        <h1 style={{textAlign:"center"}}>List of bills</h1>
        <table class="table">
            <thead>
                <tr>
                <th scope="col">#</th>
                <th scope="col">Month</th>
                <th scope="col">Year</th>
                <th scope="col">Type</th>
                <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>
                {    this.state.factures.map(facture =>(
                <tr>
                <th scope="row" >{facture.id}</th>
                <th>{facture.month}</th>
                <td>{facture.year}</td>
                <td>{facture.type}</td>
                <td><Button>to pay</Button></td>
                </tr>
                ))}

            </tbody>
        </table>

        <div
        className="d-flex align-items-center justify-content-center"
        style={{ height: "100vh" }}
      >
        <Button variant="primary" onClick={handleShow}>
          Launch Form modal
        </Button>
      </div>
      <Modal show={show}>
        <Modal.Header closeButton>
          <Modal.Title>Login Form</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <></>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary">Close Modal</Button>
        </Modal.Footer>
      </Modal>
      </div>
    );
  }
}

export default Electric;
