import { React, Component } from "react";
import { Button } from "react-bootstrap";
import { getUnits, addUser, getUsers } from "../../services/adminService";
import { getFacturesOfUser } from "../../services/elbahjaService";

class Electric extends Component {

  state = {
    users: [],
    factures:[]
  };

  async getFactures(id){
    const { data: factures } = await getFacturesOfUser(1);
    this.setState({factures:factures})
    console.log(factures);
  }

  async getUsers() {
    const { data: users } = await getUsers();
    this.setState({ users: users });
  }

  async componentDidMount() {

    await this.getFactures(1);
    console.log(this.state.factures);
  }


  render() {
    return (
      <div>
        <h1 style={{textAlign:"center"}}>List of bills</h1>
     <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">Month</th>
      <th scope="col">Year</th>
      <th scope="col">Type</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
    {    this.state.factures.map(facture =>(
      <tr>
      <th scope="row" >{facture.id}</th>
      <th>{facture.month}</th>
      <td>{facture.year}</td>
      <td>{facture.type}</td>
      <td><Button>to pay</Button></td>
    </tr>
    ))}

  </tbody>
</table>

       
      </div>
    );
  }
}

export default Electric;
